currentObject.isUuid = function(payload) {
    if (typeof payload == "string")
        return (
            payload.substr(8,1)=='-' &&
            payload.substr(13,1)=='-'&&
            payload.substr(18,1)=='-' &&
            payload.substr(23,1)=='-')
    return false;
}

// deep merge one object (source) into a target
currentObject.merge = function (target,source) {
//    node.warn("merge source")
//    node.warn(source);
    for (var item in source) {
        if (typeof source[item] === "object") {
            if (Array.isArray(source[item])) { // because arrays are objects too!
                if (typeof target[item] === "object" && !Array.isArray(target[item])) { // already existing?
                    target[item]= []; // right type (array)?
                } else {
                    if (!Array.isArray(target[item])) target[item]= [];
                }
            } else { 
                if (target.hasOwnProperty(item) && typeof target[item] === "object") { // already existing?
                    // keep existing
                } else {
                    target[item]= {};
                }
            }
            this.merge(target[item],source[item])
        } else {
            if (target[item]===undefined) target[item]=source[item]; // do not overwrite
        }
    }
//    node.warn("merge target");
//    node.warn(target);
}

currentObject.adjustTime = function (zone,point) {
