// sort a copy of an array of objects by name and keep the index
// use for display purose or one time use ony
//
// originalArray : array of objects (must include name property)
//
// return       : sorted array as copy

currentObject.saveSort = function(originalArray) {
    var sortedArray=[]; // make a copy to not mess up the index for later use
    originalArray.forEach(function(item,index){ 
        sortedArray.push(item);
    });
    // sorting should be save because it only sorts for display purpose
    sortedArray.sort(function(a,b) {
        if (a.name > b.name) return 1;
        if (a.name < b.name) return -1;
        return 0;
    });
    return sortedArray;
}
