// function to create object for specific topic
currentObject.newObject = function(topic) {
    var topicParts = topic.split('.');
    var keyword=topicParts.pop();
    if (keyword!='uiAdd') return undefined;
    if (topicParts.length<2) return undefined;
    var objectPtr=global.get(topicParts[0],"file");
    for (var i=1; i<topicParts.length; i++) {
        if (!objectPtr[topicParts[i]]) objectPtr[topicParts[i]]={}; // object not found so initialize it
        objectPtr=objectPtr[topicParts[i]];
    }
    if (!objectPtr.list) objectPtr.list=[]; // initialize list if not existing
    objectPtr.list.push({}); // add new object into list
    objectPtr.list[objectPtr.list.length-1].uuid=this.uuid();
    return objectPtr;
};

// function to get object holding the list array for specific topic or initialize the object
currentObject.getObject = function(topic) {
    if (!topic) return undefined;
    if (typeof topic === "object") return topic; // topic is already object
    var topicParts = topic.split('.');
    if (topicParts.length<2) return undefined;
    var objectPtr=global.get(topicParts[0],"file");
    if (!objectPtr) {
        node.error("getObject("+topic+") global context '"+topicParts[0]+"' not found");
        return;
    }
    for (var i=1; i<topicParts.length-1; i++) { // ignore the last/entry identifier
//        node.warn(["getObject: "+topicParts[i],objectPtr])
        if (i<topicParts.length-2 && (objectPtr[topicParts[i]] && objectPtr[topicParts[i]].hasOwnProperty("list"))) {
            // select item the uiPointer points at
            // ToDo: Make it multi user compatible using session id
//            node.warn("getObject("+topic+") "+topicParts[i]+" list["+objectPtr[topicParts[i]].uiPointer+"]!")
            objectPtr=objectPtr[topicParts[i]].list[objectPtr[topicParts[i]].uiPointer];
        } else {
            if (!objectPtr[topicParts[i]]){ 
                node.warn("getObject("+topic+") "+topicParts[i]+" initialized!")
                objectPtr[topicParts[i]]={}; // object not found so initialize it
            }
            objectPtr=objectPtr[topicParts[i]];
        }
//        node.warn(objectPtr)
    }
    if (!objectPtr.uiPointer) objectPtr.uiPointer= 0;
    if (!objectPtr.list) objectPtr.list= [];
    return objectPtr;
};

// function to get child object holding the list array for parententry or initialize a new
currentObject.getSubObject = function(parent,childName) {
    if (!parent || !childName) return undefined;

    // create child if not existing
    if (!parent.hasOwnProperty(childName)) {
        parent[childName]={
            "name": childName,
            "uiPointer": 0,
            "list": []
        }
    }

    return parent[childName];
};

// function to get object for current entry ot of list[] where the uiPointer points to 
// topic    : path to object context/object/.../entryName OR object of list location
//
// return   : found or new object

currentObject.getCurrentEntry = function(topic) {
    var objectPrt;
    try {
        if (typeof topic !== "object") objectPtr= this.getObject(topic);
            else objectPtr= topic;
        if (objectPtr.uiPointer>=objectPtr.list.length) {
            // node.warn("getCurrentEntry() corrected uiPointer!")
            objectPtr.uiPointer= objectPtr.list.length-1;
        }
        return objectPtr.list[objectPtr.uiPointer];
    } catch (err) {
        if (typeof topic === "string") node.warn("getCurrentEntry("+topic+") failed!")
            else node.warn("getCurrentEntry("+objectPrt.name+") failed!")
        return undefined;
    }
};

// function to get entry UUID by name out of list defined by topic or object. Returns first match
// topic    : path to objtypeof topicect context/object/.../entryName OR object of list location
// name     : name to search for
//
// return   : uuid of found entry or undefined

currentObject.getUuidByName = function (topic,name) {
    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;
    // node.warn(objectPtr);
    if (!objectPtr) return;
    var uuid;

    objectPtr.list.forEach(function (item) {
        if (item.name === name) {
            uuid= item.uuid;
            // node.warn(uuid);
            return;
        }
    })
    if (uuid) return uuid;
        else return undefined;
};

// function to return entry by name or uuid out of list defined by topic
// topic    : path to objtypeof topicect context/object/.../entryName OR object of list location
// name     : name or
// uuid     : uuid to search for
//
// return   : entry or undefined

currentObject.entryExists = function(topic,uuid,target) {
    if (!target && !this.isUuid(uuid)) { // to make function backwards compatible
        node.error("entryExists("+topic+","+uuid+") uuid is not a valid uuid")
        return;
    }
    if (!target) target="uuid" // use own uuid

    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;
    if (!objectPtr || !objectPtr.list) return;

    for (var i=0; i<objectPtr.list.length; i++) {
        if (objectPtr.list[i][target]===uuid) {
            return objectPtr.list[i];
        }
    }
}

// function to return index of Entry by name or uuid out of list defined by topic
// topic    : path to object context/object/.../entryName OR object of list location
// id       : name OG uuid to search for
//
// return   : entry or undefined

currentObject.getIndexOfEntry = function(topic,id) {
    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;
    if (!objectPtr || !objectPtr.list) return;

    var name,uuid;
    if (this.isUuid(id)) uuid=id;
    else name=id;

    for (var i=0; i<objectPtr.list.length; i++) {
        if ((name && objectPtr.list[i].name===name) ||
            (uuid && objectPtr.list[i].uuid===uuid)) {
            return i;
        }
    }
    return 0;
}

// function to get object for current entry by uuid out of list defined by topic
// topic    : path to objtypeof topicect context/object/.../entryName OR object of list location
// uuid     : uuid to search for amd to set if new entry is created
//
// return   : found or new object

currentObject.getEntry = function(topic,uuid,target) {
    if (!this.isUuid(uuid)) { // to make function backwards compatible
        node.error("getEntry("+topic+","+uuid+") uuid is not a valid uuid")
        return;
    }
    if (!target) target="uuid" // use own uuid
    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;
    if (!objectPtr) {
        node.error("[getEntry] base object could not be found: "+topic)
        return;
    }

    var entryPtr;
    if (!objectPtr.list) {
        node.error("[getEntry] no list property found: "+topic)
        return;
    } else {
        entryPtr= this.entryExists(objectPtr,uuid,target);
        if (entryPtr && !entryPtr.hasOwnProperty("uuid")) entryPtr.uuid=this.uuid();
    }

    if (!entryPtr) { // define new entry in list
        node.error("[getEntry] entry not found: "+uuid+" ("+target+")");
        return;
    }
    return entryPtr;
};

// function to create a new entry object
// topic    : path to objtypeof topicect context/object/.../entryName OR object of list location
// name     : name of the name to search for
//
// return   : new object

currentObject.newEntry = function(topic,name) {

    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;
    if (!objectPtr){
        node.error("[newEntry] base object could not be found: "+topic)
        return;
    }

    if (!objectPtr.list) {
        objectPtr.list=[]; // initialize list if not existing
        objectPtr.uiPointer= 0;
        objectPtr.uiPointerUuid= "";
    }

    objectPtr.list.push({}); // add new object into list
    entryPtr= objectPtr.list[objectPtr.list.length-1];
    entryPtr.name= name;
    entryPtr.uuid= this.uuid();
    node.warn("New list Entry created: "+objectPtr.name+":"+entryPtr.name);
    objectPtr.uiPointer=objectPtr.list.length-1;
    objectPtr.uiPointerUuid=entryPtr.uuid;
    return entryPtr;
};

// function to get object for current entry by index number out of list defined by topic
currentObject.getEntryByIndex = function(topic,index) {
    var objectPrt;
    if (typeof topic === "string") objectPtr=this.getObject(topic);
        else objectPtr= topic;

    if (objectPtr && objectPtr.list) {
        return objectPtr.list[index];
    } else return undefined;
};

// function to delete current entry out of list
// topic        :   path to objtypeof topicect context/object/.../entryName OR object of list location
// index        :   uuid or
//                  index of entry to delete (uiPointer if undefined)

currentObject.deleteEntry = function(topic,index) {
    var objectPrt;
    try {
        if (typeof topic !== "object") objectPtr=this.getObject(topic);
            else objectPtr= topic;
        node.warn("[deleteEntry]");
        node.warn(objectPtr);
        if (this.isUuid(index)) { // find index id uuid is given
            index=this.getIndexOfEntry(objectPtr,index);
        }
        if (index === undefined) index=objectPtr.uiPointer;
        if (index<objectPtr.list.length){
            var returnObj= objectPtr.list.splice(index,1);
            node.warn("[deleteEntry] Index :"+index+"("+returnObj.name+") deleted ("+objectPtr.list.length+" items left)");
            if (returnObj) objectPtr.uiPointer=0;
            return returnObj;
        } else return undefined;
    } catch (err) {
        if (typeof topic === "string") node.warn("deleteEntry("+topic+") failed!")
            else node.warn("deleteEntry("+objectPrt.name+") failed!")
        return undefined;
    }
};

// function to insert a list entry left from uiPointer and gives back the new Object
// entryObject  : path to object context/object/.../entryName OR object of list location
// index        : index of entry to delete (uiPointer if undefined)
currentObject.insertItemLeft = function(topic,index) {
    var objectPrt;
    try {
        if (typeof topic !== "object") objectPtr=this.getObject(topic);
            else objectPrt= topic;
        if (index === undefined) index= objectPtr.uiPointer;
        objectPrt.list.splice(index,0,{});
        return objectPrt.list[index];
    } catch (err) {
        if (typeof topic === "string") node.warn("insertItemLeft("+topic+") failed!")
            else node.warn("insertItemLeft("+objectPrt.name+") failed!")
        return undefined;
    }
}

