// handle messages from ui-control node
switch (msg.payload) {
    case "lost" : // socket lost so clean temp settings
        if (uiControlTemp.clients.hasOwnProperty(msg.socketid)) {
            delete uiControlTemp.clients[msg.socketid];
            status={fill:"blue",shape:"dot",text:"settings deleted"};
            return [{payload:status},null,null];
        }
        break;
    case "OK":
    case "change":
    case "connect":
        msgOut.socketid=msg.socketid;
        msgToInput.push(RED.util.cloneMessage(msg));
        msgToInput[msgToInput.length-1].command="state"
        msgToInput[msgToInput.length-1].uiNode=uiNode;
        msgToInput[msgToInput.length-1].configContext=configContext;
        msgToInput[msgToInput.length-1].multiUser=multiUser;

        if (msg.name===env.get("tabName") || msg.payload==="connect") {
            // load default settings into UI
            if (!config.hasOwnProperty("params")) config.params=RED.util.cloneMessage(env.get("defaultConfig"));
            Object.keys(config.params.uiParmeters).forEach(function (key) {
                msgToUI.push({"topic":key,"socketid":msg.socketid,"payload":config.params.uiParmeters[key]});
            });

            // set ui_control defaults
            if (env.get("ui_control")) {
                msgOut.ui_control=env.get("ui_control");
                status={fill:"green",shape:"dot",text:"defaults loaded"};
            }

            var updateCounter = 0;
            var columnID="";

            // order columns if modified in runtime
            // not the most elegant programming style: Improvements welcome ;)

            if (config.hasOwnProperty("columnOrder")){

                var addedColumns = 0;
                var sortColumnsByLayout = function (sortColumns, columnsLayout, targetColumns) {
                    for (var layoutColumn=0;  layoutColumn<columnsLayout.length; layoutColumn++) {
                        for (var sortColumn in sortColumns) {
                            if (sortColumns[sortColumn].hasOwnProperty("columns")) {
                                targetColumns.push({"title":sortColumns[sortColumn].title, "columns":[]});
                                sortColumnsByLayout(sortColumns[sortColumn].columns,columnsLayout,targetColumns[targetColumns.length-1].columns);
                                layoutColumn=addedColumns; // jump forward after childes added
                            } else {
                                if (columnsLayout[layoutColumn].field===sortColumns[sortColumn].field){
                                    targetColumns.push(sortColumns[sortColumn]);
                                    addedColumns++;
                                    break;
                                }
                            }
                        }
                    }
                }                 

                var newColumns=[];
                sortColumnsByLayout(msgOut.ui_control.tabulator.columns,config.columnOrder,newColumns);
                msgOut.ui_control.tabulator.columns=newColumns;
            }

            var updateColumns = function(columns,property) {
                // add or modifiy properties if modified in runtime
                for (var column of columns) {
                    if (column.hasOwnProperty("columns") && Array.isArray(column.columns)) {
                        updateColumns(column.columns,property);
                    } else {
                        columnID=column.field+":"+column.title;
                        if (config.hasOwnProperty(columnID) &&
                            config[columnID].hasOwnProperty(property)) {

                            column[property]=config[columnID][property]
                            updateCounter++;
                        }
                    }
                }
            }

            updateColumns(msgOut.ui_control.tabulator.columns,"width")
            if (updateCounter>0) {
                status.fill="green";
                status.shape="dot";
                status.text+=" ("+updateCounter+ " updates)";
            }

            // restore stored table Data
            if (uiControl.hasOwnProperty("tableData")) {
                msgOut.payload=uiControl.tableData;
                // set counter & first row to match restored data
                if (uiControl.tableData.length>0) {
                    config.params.counter=uiControl.tableData[0].id;
                    config.params.firstRow=uiControl.tableData[uiControl.tableData.length-1].id;
                }                
                status.text+=msgOut.payload.length + " rows"
            } else {
                config.params.counter=0;
                config.params.firstRow=0;
                msgOut.payload=[]; // send empty array to clear table
                msgToInput.push({"command":"resetCounter","payload":0});
            }

            // update filter List
            if (uiControl.hasOwnProperty("filterList")) {
                msgToUI.push({"topic":"filterString","socketid":msg.socketid,"options":uiControl.filterList});
            }

            config.params.visible=true;
            return [{payload:status},msgOut,msgToInput,msgToUI];
        }

        // send online/offline messages if tab state changes
        if (msg.name===env.get("tabName")) {
            config.params.visible=true;
            status={fill:"green",shape:"dot",text:"online"};
        } else {
            config.params.visible=false;
            status={fill:"yellow",shape:"dot",text:"offline"};
        }

        return [{payload:status},null,msgToInput,msgToUI];
    default:
        node.error({"payload:":msg.payload,"tab:":env.get("tabName")})
        status={fill:"red",shape:"dot",text:msg.payload+" unhandeled"};
        return [{payload:status},null,null,msgToUI];
}

