contextStore.pushValueToChart = function (chart,serie,label,value,maxTime,maxMsgs) {
//    node.warn({"function":"pushValueToChart","chart":chart,"serie":serie,"label":label,"value":value,"maxTime":maxTime,"maxMsgs":maxMsgs})

    if (maxTime===undefined) maxTime=1440 // = 24h
    if (maxMsgs===undefined) maxMsgs=1500 // = 1 msg per minute ~ 14h
    if (!chart.series.includes(serie)) { // init chart if nessesary
        chart.series.push(serie);
        chart.data.push([]);
        chart.label.push(label);
    }
    let dataColumn=chart.data[currentChart.series.indexOf(serie)];
    let dateNow=Date.now();

    // add new line to data
    dataColumn.push({"x":dateNow,"y":value});

    // delete lines if exeeds maxMsgs
    if (dataColumn.length>maxMsgs) {
        dataColumn.splice(0,dataColumn.length-maxMsgs);
    }
    // delete lines if older than maxTime
    let timeLimit=dateNow-maxTime*60000;
    let j=0;
    while (dataColumn[j]!==undefined && dataColumn[j].x<timeLimit) j++;
    if (j>0) dataColumn.splice(0,j);
}

