// definition of calculation functions these can be configured in config json
// all accumulationg form latest back to time limit
var calc={};

calc["min"] = function(chartData,timeLimit) {
    let min=Number.MAX_VALUE;
    for (let i=chartData.length-1; i>-1; --i) {
        if (chartData[i].y<min) min=chartData[i].y;
        if (chartData[i].x<timeLimit) break;
    }
    return min;
}

calc["max"] = function(chartData,timeLimit) {
    let max=-Number.MAX_VALUE;
    for (let i=chartData.length-1; i>-1; --i) {
        if (chartData[i].y>max) max=chartData[i].y;
        if (chartData[i].x<timeLimit) break;
    }
    return max;
}

calc["avg"] = function(chartData,timeLimit) {
    let sum=0, count=0;
    for (let i=chartData.length-1; i>-1; --i) {
        sum+=chartData[i].y;
        count++;
        if (chartData[i].x<timeLimit) break;
    }
    return sum/count;
}

calc["quardAvg"] = function(chartData,timeLimit) {
    let sum=0; i=chartData.length-1;
    let endTime=chartData[i].x;
    let lastTime=endTime;
    for (i; i>-1; --i) {
        sum+=chartData[i].y*(lastTime-chartData[i].x);
        lastTime=chartData[i].x;
        if (chartData[i].x<timeLimit) break;
    }
    if (i<0) i=0;
//    node.warn(["qAvrg",chartData,timeLimit,endTime,i,sum])
    let divider=endTime-chartData[i].x;
    if (divider===0) divider=1;
    return sum/(divider);
}

calc["delta"] = function(chartData,timeLimit) {
    let i=0;
    for (i=chartData.length-1; i>-1; --i) {
        if (chartData[i].x<timeLimit) break;
    }
    if (i<0) i=0;
    return chartData[chartData.length-1].y-chartData[i].y;
}

calc["trend"] = function(chartData,timeLimit) {
    let i=0;
    for (i=chartData.length-1; i>-1; --i) {
        if (chartData[i].x<timeLimit) break;
    }
    if (i<0) i=0;
    return chartData[chartData.length-1].y-chartData[i].y;
}

calc["total"] = function(chartData,timeLimit) {
    let total=0
    for (let i=chartData.length-1; i>-1; --i) {
        total+=chartData[i].y;
        if (chartData[i].x<timeLimit) break;
    }
    return total;
}
