var status = {fill:"yellow",shape:"dot",text: "no msg.state"};
if (msg.hasOwnProperty("state")) {
    status.text="device=";
    status.text+=(msg.state.$name) ? msg.state.$name : msg.topic;
    var formatHtml=function(icon) {
        var html=""
        if (icon.icon.startsWith('fa')) {
            html = "<i class=\""+icon.icon+'\"></i>'; 
        } else {
            html+='<i class="material-icons"';
            if (icon.hasOwnProperty("style")) html+=' style="'+icon.style+'"';
            html+='>'+icon.icon+'</i>';                               
        }
        return html;
    }
    nextProperty: 
    for (var state in msg.state) {
        if (icons.hasOwnProperty(state)) {
            for (var icon of icons[state]) {
                switch (typeof msg.state[state]) {
                    case "string":
                        if (msg.state[state]===icon.value) {
                            msg.state[state+"Icon"]=formatHtml(icon);
                            status.fill="green";
                            status.text+=state+" ";
                            continue nextProperty;
                        }
                        break;    
                    case "number":
                        if (msg.state[state]<=icon.value) {
                            msg.state[state+"Icon"]=formatHtml(icon);
                            status.fill="green";
                            status.text+=state+" ";
                            continue nextProperty;
                        }
                        break;
                }
            }
        }
    }    
}
node.status(status);
return msg;

// IMPORTTANT! 
// Make shure that the topic matches the topit of homie state node that the extra data is merged correctly!
msg.topic="mqtt://192.168.2.14:1883/homie/"+msg.deviceId;
// if you do not use the homie convention choose a unique indetifier as msg.topic:
// msg.topic=msg.payload.System["Unit Name"];

msg.state={"$name":msg.deviceId,"$state":"ready"};
switch (msg.propertyId.toLowerCase()) {
    // messages that need conversion
    case ('rssi'):
        msg.state.signal=Math.min(Math.max(2 * (msg.value + 100), 0), 100)
        break;
    // all messages that can be translated 1:1    
    case ('freeheap'):
    case ('uptime'):
        msg.state[msg.propertyId]=msg.value;
        break;
    default: // drop message
        return;
}
return msg;

if (msg.hasOwnProperty("payload") && msg.payload.hasOwnProperty("command")) {
    switch (msg.payload.command) {
        case "deleteTable":
            context.set("lastSeen",undefined);
            context.set("lastState",undefined);
            node.status({fill:"yellow",shape:"dot",text:"lastSeen&lastState deleted"});
            node.warn("[lastSeen] lastSeen&lastState deleted");
            return msg;
    }
}

if (msg.hasOwnProperty("topic") && (msg.hasOwnProperty("state") || msg.payload.hasOwnProperty("command"))) {
    var lastSeen = context.get("lastSeen");
    if (lastSeen===undefined) {
        lastSeen={};
        context.set("lastSeen",lastSeen);
    }
    var lastState = context.get("lastState");
    if (lastState===undefined) {
        lastState={};
        context.set("lastState",lastState);
    }

    var pad = function (num) {
        return ("0"+num).slice(-2);
    };
    var formatLastSeen = function(lastSeen){
        if (lastSeen>0) {
            var secs = Math.floor((Date.now() - lastSeen) / 1000);
            var minutes = Math.floor(secs / 60);
            secs = secs%60;
            var hours = Math.floor(minutes/60);
            minutes = minutes%60;
            var days = Math.floor(hours/24);
            hours = hours%24;
            if (days>0) {
                return days+"days "+hours+"hours ago";
            } else if (hours>0) {
                return pad(hours)+":"+pad(minutes)+" ago";
            } else if (minutes>0) {
                return minutes+"min ago";
            } else {
                return secs+"s ago";
            }
        }
        return "n/a";
    }

    if (msg.hasOwnProperty("state")) {
        if (msg.state.hasOwnProperty("$state")) {
            // do not add timestamp if $status=="lost" (this is the LWT form the broker)
            if (!lastSeen.hasOwnProperty(msg.topic)) lastSeen[msg.topic]={};
            lastState[msg.topic]=msg.state.$state;

            if (msg.state.$state==="lost"){
                if (lastSeen.hasOwnProperty(msg.topic)) {
                    msg.state.lastSeenreadyFormatted="lost "+formatLastSeen(lastSeen[msg.topic].ready);
                } else {
                    msg.state.lastSeenreadyFormatted="never seen";
                }
            } else {
                msg.state["lastSeen"+msg.state.$state]=Date.now();
                msg.state["lastSeen"+msg.state.$state+"Formatted"]=formatLastSeen(lastSeen[msg.topic][msg.state.$state]);
                lastSeen[msg.topic][msg.state.$state]=msg.state["lastSeen"+msg.state.$state];
            }
        }
    } else if (msg.payload.hasOwnProperty("command")) {
        switch (msg.payload.command) {
            case "updateData":
                msg.payload.arguments=[[]];
                for (let topic in lastSeen) {
                    if (lastSeen.hasOwnProperty(topic)) {
                        let lineData={"$topic":topic};
                        for (let value in lastSeen[topic]) {
                            if (lastSeen[topic].hasOwnProperty(value)) {
                                lineData["lastSeen"+value]=lastSeen[topic][value];
                                lineData["lastSeen"+value+"Formatted"]=formatLastSeen(lastSeen[topic][value]);
                            }
                        }
                        if (lastState[topic]==="lost") {
                            if (lastSeen[topic].hasOwnProperty("ready")) {
                                lineData["lastSeenreadyFormatted"]="lost "+formatLastSeen(lastSeen[topic].ready); 
                            } else {
                                lineData["lastSeenreadyFormatted"]="never seen";
                            }
                        }
                        msg.payload.arguments[0].push(lineData);
                    }
                }
                break;
            default:
