var average = function (property) {
    let sum = 0;
    let count = 0;
    let avg = 0;
    let i = 0;
    Object.keys(values).forEach((topic) => {
        if (values[topic].hasOwnProperty(property) && typeof values[topic][property] === "number") {
            sum += values[topic][property];
            i++;
        }
    });
    if (i>0) {
        avg=Math.floor((sum / i) * 100) / 100;
    }
    return avg;
};

