const delay = 60;
var tableData = flow.get("tableData","file");
if (tableData===undefined) return msg;
let tableConfig = flow.get("tableConfig","file");
let tableLength = Object.keys(tableData).length;
var sprinklerStatus=flow.get("sprinklerStatus","file");
if (sprinklerStatus===undefined) {
    sprinklerStatus={status:"stop",position:0};
    flow.set("sprinklerStatus",sprinklerStatus);
}
var msgOut = {};
var currentValve = {};
var attr=[];

var setState = function (state) {
    sprinklerStatus.status=state;
    node.send([null,null,null,{topic:"state",payload:state}]);
};

var confirmCommand = function (command) {
    sprinklerStatus.command=command;
    // node.send([null,null,null,[{topic:"command",payload:command},{topic:"command/set"}]]);
};

var resetTable = function () {
    let msgTable={};
    sprinklerStatus.totalRuntime=0;
    Object.keys(tableData).forEach((key) => {
        sprinklerStatus.totalRuntime+=tableData[key].interval*60;
        msgTable={topic:tableData[key].id};
        msgTable.valve={"id":msgTable.topic,"stateIcon":'<i class="fa fa-pause-circle"></i>',"runtime":tableData[key].interval*60,"donePercent":0};
        node.send([null,null,msgTable,null]);        
    });
    node.send([null,null,null,{topic:"progress",payload:0}]);
    sprinklerStatus.timeLeft=sprinklerStatus.totalRuntime;
};

var getPosition = function(id){
    let found=0;
    Object.keys(tableData).forEach((valve,index) => {
        if (tableData[valve].id===id) {
            found=index;
        }
    });
    return found;
};

var getValve = function(position) {
    if (tableConfig && tableConfig.hasOwnProperty("rowOrder")) {
        let progOrder=tableConfig.rowOrder;
        return tableData[progOrder[position]];
    } else {
        return tableData[Object.keys(tableData)[position]];
    }
};

var setPump = function(state) {
    node.send([null,{topic:"pump",payload:state},null]);
};


var startValve = function(valveId,time,customDelay=0) {
    msgTable={topic:valveId};
    msgTable.valve={"id":msgTable.topic,"stateIcon":'<i class="fa fa-pause-circle"></i>',"runtime":time,"donePercent":0};
    node.send([null,null,msgTable,null]);        
    sprinklerStatus.controller=Math.floor(valveId/100);
    sprinklerStatus.valveNr=valveId%100;
    node.warn([valveId,sprinklerStatus]);
    msgOut=RED.util.cloneMessage(sprinklerStatus);

    msgOut.topic="SYSTEM/cmd";
    msgOut.payload="event,run="+time+","+sprinklerStatus.valveNr;
    msgOut.controller=sprinklerStatus.controller;
    setPump(true);
    setState("running");
    if (customDelay>0) {
        node.status({fill:"yellow",shape:"dot",text:"waiting for pump "+customDelay+" sec"});
        setTimeout(function () {
            node.status({fill:"green",shape:"dot",text:"start pos:"+sprinklerStatus.position+" (C:"+sprinklerStatus.controller+" V:"+sprinklerStatus.valveNr+" "+time+" min)"});
            node.warn([msgOut,{topic:"start",enabled:false},null,null]);
            node.send([msgOut,{topic:"start",enabled:false},null,null]);
        }, customDelay*1000);
    } else {
        node.send([msgOut,{topic:"start",enabled:false},null]);
        node.status({fill:"green",shape:"dot",text:"start pos:"+sprinklerStatus.position+" (C:"+sprinklerStatus.controller+" V:"+sprinklerStatus.valveNr+" "+time+" min)"});
    }
};

var startProg = function(position) {
    do {
        sprinklerStatus.valve=getValve(position);
        if (sprinklerStatus.valve.interval===0) position++;
    } while (sprinklerStatus.valve.interval===0 && position<tableLength);


    if (sprinklerStatus.valve!==undefined && position<tableLength) { 
        sprinklerStatus.position=position;
        sprinklerStatus.runTime=sprinklerStatus.valve.interval;
        sprinklerStatus.valveConfirmed=false;
        sprinklerStatus.timeConfirmed=false;

        if (sprinklerStatus.status!=="running") {
            node.send([null,{topic:"start",enabled:false},null]);
            setPump(true);
            startValve(sprinklerStatus.valve.id,sprinklerStatus.runTime,delay);
        } else {
            startValve(sprinklerStatus.valve.id,sprinklerStatus.runTime,0);
        }
    } else {
        stopValves();
        sprinklerStatus.position=0;
        node.send([null,{topic:"start",enabled:true},null]);
        // node.status({fill:"green",shape:"dot",text:"stopped"});
    }
};

var stopValve = function(controller) {
    msgOut.topic="SYSTEM/cmd";
    msgOut.payload="event,closeAllValves";
    msgOut.controller=controller;
    setState("stop");
    node.status({fill:"yellow",shape:"dot",text:"stop controller: "+controller});
    setPump(false);
    node.send([msgOut,{topic:"start",enabled:true},null]);
};

var stopValves = function() {
    let stopped="";
    let msgOut={
        topic:"SYSTEM/cmd/set",
        payload:"event,closeAllValves"
    };
    for (let i=1; i<4; i++) {
        msgOut.controller=i;
        node.send([msgOut,null,null]);
        stopped+=i+" ";
    }
    setState("stop");
    node.status({fill:"yellow",shape:"dot",text:"stoped: "+stopped});
    setPump(false);
    node.send([null,{topic:"start",enabled:true},null]);
};

