switch (msg.topic){
    case 'homie/Node-RED/sprinklerSystem/program':
        node.warn("switch program "+msg.payload);
        break;
    case "startThisFor":
        setState("preparing");
        node.warn(["startThisFor",attr]);
        context.set("onlyOneValve",true);
        sprinklerStatus.totalRuntime=Number(attr[1])*60;
        sprinklerStatus.timeLeft=sprinklerStatus.totalRuntime;
        startValve(attr[0],Number(attr[1]),delay);
        break; 
    default:
        switch (msg.payload.toLowerCase()){
            case "[new]":
                break;
            case "start":
                if (sprinklerStatus.status!=="running") {
                    setState("preparing");
                    resetTable();
                    confirmCommand("start");
                    startProg(0);
                }
                break;
            case "startthis":
                setState("preparing");
                context.set("onlyOneValve",true);
                let position=getPosition(attr[0]);
                sprinklerStatus.totalRuntime=tableData[position].interval*60;
                sprinklerStatus.timeLeft=sprinklerStatus.totalRuntime;
                startProg(position);
                break;
            case "startfromhere":
                stopValves();
                setState("preparing");
                confirmCommand("start");
                startProg(getPosition(attr[0]));
                break;
            case "stop":
                stopValves();
                setState("stop");
                confirmCommand("stop");
                break;
            case "pause":
                setState("pause");
                stopValve(sprinklerStatus.controller);
                confirmCommand("pause");
                break;
            case "next":
                if (context.get("onlyOneValve")) {
                    stopValves();
                    context.set("onlyOneValve",undefined);
                    stopValves();
                } else {
                    if (sprinklerStatus.status==="running" && sprinklerStatus.position<tableLength) {
                        sprinklerStatus.position++
                        startProg(sprinklerStatus.position);
                        confirmCommand("next");
                    } else {
                        stopValves();
                    }
                }
                break;
            default:
                if (msg.propertyId==="runTime") {
                    node.status({fill:"blue",shape:"dot",text:"running "+msg.value+"s ("+sprinklerStatus.controller+"/"+sprinklerStatus.valveNr+" "+sprinklerStatus.runTime+" min)"});
                } else {
                    node.status({fill:"red",shape:"dot",text:"unknown msg.payload="+msg.payload});
                }
                break;
        }
}
flow.set("sprinklerStatus",sprinklerStatus,"file");
return;

var sprinklerStatus=flow.get("sprinklerStatus","file");
var status={fill:"yellow",shape:"dot",text:"unknown state"};
if (sprinklerStatus===undefined) return;
const stateIcon = {
    "stop":'<i class="fa fa-wrench"></i>',
    "preparing":'<i class="fa fa-stop"></i>',
    "running":'<i class="fa fa-spinner fa-spin"></i>',
    "error":'<i class="fa fa-exclamation-triangle"></i>',
    "done":'<i class="fa fa-check-square"></i>'
}

var id = sprinklerStatus.controller*100+sprinklerStatus.valveNr;
var msgOut={topic:id};
switch(msg.propertyId) {
    case "runTime":
        if (sprinklerStatus.status==="running") {
            let donePercent = 100-(100/(sprinklerStatus.runTime*60)*msg.value);
            sprinklerStatus.timeLeft-=10;
            let donePercentTotal=100-(sprinklerStatus.timeLeft/sprinklerStatus.totalRuntime*100);
            if (msg.value>0) {
                msgOut.valve={"id":id,"stateIcon":stateIcon[sprinklerStatus.status],"runtime":msg.value,"donePercent":donePercent.toFixed(2)};
                status.text=`${id} remaining: ${msg.value}s`;
                node.status(status);
                return [msgOut,null,{topic:"progress",payload:donePercentTotal.toFixed(1)}];
            } else {
                msgOut.valve={"id":id,"stateIcon":stateIcon["done"],"runtime":msg.value,"donePercent":donePercent.toFixed(2)};
                status.text=`${id} done! ${msg.value}s`;
                node.status(status);
                return [msgOut,null,{topic:"progress",payload:donePercentTotal.toFixed(1)}];
            }
        }
        break;
    case "finished":
        if (sprinklerStatus.status==="running" && sprinklerStatus.valveNr===msg.value) {
            status.fill="green";
            status.text=`${msg.value} finished`;
            node.status(status);
            return [msgOut,{payload:"next"},null];
        }
        break;
    default:
        msgOut.valve={"id":id,"stateIcon":stateIcon[sprinklerStatus.status]};
        status.text=`${id} state: ${sprinklerStatus.status}`
        node.status(status);
        return [msgOut,null];
}
return;

