if (msg.name && (msg.name!=="Bewässerung" && msg.name!=="Garten")) return;
var sprinklerPrograms = flow.get("sprinklerPrograms","file") || {};
var sprinklerStatus= flow.get("sprinklerStatus","file") || {
    "valve":{},
    "status":"stop",
    "position":0,
    "runTime":0,
    "controller":0,
    "valveNr":0};
// options for dropdown
msg.options=Object.keys(sprinklerPrograms) || [];
msg.options.push('[new]');
if (!sprinklerStatus.currentProgram ||
    !msg.options.includes(sprinklerStatus.currentProgram)) {

    sprinklerStatus.currentProgram=msg.options[0];
    msg.topic="program/set";
} else {
    msg.topic="program";
}
msg.payload=sprinklerStatus.currentProgram;
// options for homie
msg.homie={
        "$properties": {
            "program":{
                "$format": Object.keys(sprinklerPrograms).toString(),
                "$settable": true
            }
    }
};
flow.set("sprinklerStatus",sprinklerStatus,"file");
return msg;

if (msg.payload.hasOwnProperty("command")) {
    var sprinklerPrograms = flow.get("sprinklerPrograms","file") || {};
    switch (msg.payload.command) {
        case 'newProgram':
            if (!sprinklerPrograms.hasOwnProperty(msg.payload.input)) {
                sprinklerPrograms[msg.payload.input]={
                    id:RED.util.generateId()
                }
                flow.set("sprinklerPrograms",sprinklerPrograms,"file");
            }
            delete msg.topic;
            msg.payload.command='getTable';
            msg.payload.source='newProgram';
            return [msg,null];
        case 'getTable':
            if (msg.payload.source==='newProgram') {
                let programName=msg.payload.input;
                sprinklerPrograms[programName].tableArray=msg.payload.tableArray;
                flow.set("sprinklerPrograms",sprinklerPrograms,"file");
                msg.topic='updateDropdown';
                msg.payload=programName;
                return [null,msg];
            }
    }
}

if (msg.hasOwnProperty('ui_control') && msg.ui_control.hasOwnProperty('callback')) return;
var sprinklerPrograms = flow.get("sprinklerPrograms","file") || {};
var sprinklerStatus = flow.get("sprinklerStatus","file") || {};

var setProgram = function (programm) {
    sprinklerStatus.currentProgram=programm;
    // flow.set("sprinklerStatus",sprinklerStatus);
    node.send([null,null,{topic:"program/set",payload:programm}]);
}

if (msg.payload.hasOwnProperty("command")) {
    switch (msg.payload.command) {
        case 'getTable':
            if (msg.payload.source==='switchProgram') {
                let programName=msg.payload.input;
                sprinklerPrograms[sprinklerStatus.currentProgram].tableArray=msg.payload.tableArray;
                flow.set("sprinklerPrograms",sprinklerPrograms,"file");
                msg.payload=RED.util.cloneMessage(sprinklerPrograms[programName].tableArray);
                delete msg.ui_control;
                setProgram(programName);
                flow.set("sprinklerStatus",sprinklerStatus);
                node.status({fill:"green",shape:"dot",text:"Program: "+sprinklerStatus.currentProgram});
                return [msg,null];
            }
    }
} else {
    if (sprinklerPrograms.hasOwnProperty(msg.payload)) {
        if (sprinklerStatus.currentProgram) { // save current Program
            delete msg.topic;
            let newProgram = msg.payload;
            msg.payload={command:'getTable',source:'switchProgram',input:newProgram};
            node.status({fill:"yellow",shape:"dot",text:"getTable : "+sprinklerStatus.currentProgram});
            return [msg,null];
        } else {
            setProgram(msg.payload);
            node.status({fill:"green",shape:"dot",text:"Program: "+sprinklerStatus.currentProgram});
            msg.payload=RED.util.cloneMessage(sprinklerPrograms[msg.payload].tableArray);
            delete msg.ui_control;
            return [msg,null];
        }
    } else {
        node.status({fill:"red",shape:"dot",text:"Program: "+msg.payload+" undefined"});
    }
}

