timeformat= function(cell, formatterParams, onRendered) {
    var pad = function (num) {
        return ('0'+num).slice(-2);
    };     
    var secs = Number(cell.getValue());
    if (Number.isNaN(secs)) return;
    var minutes = Math.floor(secs / 60);
    secs = secs%60;
    var hours = Math.floor(minutes/60);
    minutes = minutes%60;
    var days = Math.floor(hours/24);
    hours = hours%24;
    if (days>0)
        return days+'d '+pad(hours)+':'+pad(minutes);
    else
        return pad(hours)+':'+pad(minutes)+':'+pad(secs); 
}

avgPercentage = function(values, data, calcParams){
    var calc = 0;
    var count = 0;
    data.forEach(function(value){
        if(value.interval > 0){
            calc+=Number(value.donePercent);
            count++;
        }
    });
    if (count>0) calc=(calc/count).toFixed(2);
    calc=calc+' %';
    return calc;
}

sumTime = function(values, data, calcParams){
    var secs = 0;
    var pad = function (num) {
        return ('0'+num).slice(-2);
    };

    values.forEach(function(value){
        secs+=Number(value);
    });

    var minutes = Math.floor(secs / 60);
    secs = secs%60;
    var hours = Math.floor(minutes/60);
    minutes = minutes%60;
    return pad(hours)+':'+pad(minutes)+':'+pad(secs);
}

