const maxStore = 10;
var store = context.get("store") || {};
if (!store.hasOwnProperty(msg.input.topic)) {
    store[msg.input.topic]={};
}
var msgOut={topic:msg.input.topic, unitId:msg.input.unitId};
var msgChart={topic:msg.input.topic, unitId:msg.input.unitId};
var msgTotal={topic:"PZEM-016", payload:{}};
var currentStore=store[msg.input.topic];
if (!currentStore.hasOwnProperty("lastReading")) currentStore.lastReading={};
var lastReading=currentStore.lastReading;

var total= function(value){
    var result=0;
    Object.keys(store).forEach((sensor) => {
        result+=store[sensor].lastReading[value];
    });
    return result;
};

var avg= function(data,property) {
    let sum=0;
    data.forEach((reading) => {
        sum+=reading[property];
    });
    return sum/data.length;
};

var storeChart= function(data) {
    if (!currentStore.hasOwnProperty("data")) currentStore.data=[];
    currentStore.data.push(data);
    if (currentStore.data.length>maxStore) {
        var sample={
            voltage: avg(currentStore.data,"voltage"),
            current: avg(currentStore.data,"current"),
            power: avg(currentStore.data,"power"),
            frequency: avg(currentStore.data,"frequency"),
            powerFaktor: avg(currentStore.data,"powerFaktor")
        };
        currentStore.data=[];
        return sample;
    } 
    return {};
};

